sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {


	if(g_form.isNewRecord()) {
		if(newValue !== oldValue) {
			getEmailClientScript();
		}
	}

   if (isLoading || newValue === '' || newValue === oldValue) {
      return;
   }

	function getEmailClientScript() {

		var getEmailAddress = new GlideAjax('GetEmailAddress');
		getEmailAddress.addParam('sysparm_name', 'getEmail');
		getEmailAddress.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
		getEmailAddress.getXML(populateEmail);

	}

	function populateEmail(response) {
		// Extract the email address from the response, clear any value from the email field, 
		// set new value in the email field
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_request_for_email');
		g_form.setValue('u_request_for_email',emailFromScriptInclude);
	}

	getEmailClientScript();

}
